import React from "react";
import { Draggable } from "@hello-pangea/dnd";
import { FaEdit, FaTrash } from "react-icons/fa"; // Ícones de edição e exclusão

const Tarefa = ({ tarefa, index, onEdit, onDelete }) => {
  const custoNumerico = Number(tarefa.custo); // Converte custo para número

  const estiloDestaque =
    custoNumerico >= 1000
      ? { backgroundColor: "#fff9c4", fontWeight: "bold" } // Destaque para custo >= 1000
      : { backgroundColor: "#f0f0f0" }; // Estilo normal

  // Função para confirmar exclusão
  const handleDelete = () => {
    const confirm = window.confirm(
      `Você tem certeza que deseja excluir a tarefa "${tarefa.nome}"?`
    );
    if (confirm) {
      onDelete(); // Chama a função de exclusão passada como prop
    }
  };

  return (
    <Draggable draggableId={tarefa.id.toString()} index={index}>
      {(provided) => (
        <div
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          ref={provided.innerRef}
          style={{
            display: "flex",
            justifyContent: "space-between", // Posiciona o conteúdo e os botões
            alignItems: "center",
            margin: "8px 0",
            padding: "16px",
            borderRadius: "4px",
            ...estiloDestaque, // Aplica o estilo condicional
            ...provided.draggableProps.style,
          }}
        >
          {/* Conteúdo da Tarefa */}
          <div style={{ textAlign: "left", flex: 1 }}>
            <h3 style={{ marginBottom: "8px" }}>{tarefa.nome}</h3>
            <p>ID: {tarefa.id}</p>
            <p>Custo: R$ {custoNumerico.toFixed(2)}</p>
            <p>Data Limite: {tarefa.dataLimite}</p>
          </div>

          {/* Botões */}
          <div style={{ display: "flex", gap: "8px" }}>
            <button
              onClick={onEdit}
              style={{
                border: "none",
                background: "transparent",
                cursor: "pointer",
              }}
            >
              <FaEdit size={20} color="#4CAF50" title="Editar" />
            </button>
            <button
              onClick={handleDelete} // Adiciona a confirmação de exclusão
              style={{
                border: "none",
                background: "transparent",
                cursor: "pointer",
              }}
            >
              <FaTrash size={20} color="#f44336" title="Excluir" />
            </button>
          </div>
        </div>
      )}
    </Draggable>
  );
};

export default Tarefa;
