// frontend/src/components/FormularioTarefa.js
import React, { useState } from "react";
import api from "../services/api";

const FormularioTarefa = ({ tarefa, onClose }) => {
  const [nome, setNome] = useState(tarefa.nome || "");
  const [custo, setCusto] = useState(tarefa.custo || "");
  const [dataLimite, setDataLimite] = useState(
    tarefa.dataLimite
      ? new Date(tarefa.dataLimite).toISOString().substr(0, 10)
      : ""
  );
  const [erro, setErro] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErro("");
    try {
      if (tarefa.id) {
        // Editar
        await api.put(`/tarefas/${tarefa.id}`, {
          nome,
          custo,
          dataLimite,
        });
      } else {
        // Incluir
        await api.post("/tarefas", {
          nome,
          custo,
          dataLimite,
        });
      }
      onClose();
    } catch (error) {
      setErro(
        error.response?.data?.error ||
          "Erro ao salvar tarefa. Verifique os dados."
      );
    }
  };

  return (
    <div className="modal">
      <form onSubmit={handleSubmit}>
        <h2>{tarefa.id ? "Editar Tarefa" : "Incluir Tarefa"}</h2>
        {erro && <p className="erro">{erro}</p>}
        <div>
          <label>Nome:</label>
          <input
            type="text"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Custo:</label>
          <input
            type="number"
            value={custo}
            onChange={(e) => setCusto(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Data Limite:</label>
          <input
            type="date"
            value={dataLimite}
            onChange={(e) => setDataLimite(e.target.value)}
            required
          />
        </div>
        <button type="submit">Salvar</button>
        <button type="button" onClick={onClose}>
          Cancelar
        </button>
      </form>
    </div>
  );
};

export default FormularioTarefa;
