import React, { useState } from "react";
import Modal from "react-modal"; // Importa React Modal
import api from "../services/api";

// Estilos do modal
const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    padding: "20px",
    borderRadius: "10px",
    backgroundColor: "#fff",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
  },
};

Modal.setAppElement("#root"); // Necessário para acessibilidade

const FormularioTarefa = ({ tarefa, onClose }) => {
  const [nome, setNome] = useState(tarefa.nome || "");
  const [custo, setCusto] = useState(tarefa.custo || "");
  const [dataLimite, setDataLimite] = useState(
    tarefa.dataLimite
      ? new Date(tarefa.dataLimite).toISOString().substr(0, 10)
      : ""
  );
  const [erro, setErro] = useState("");
  const [modalIsOpen, setModalIsOpen] = useState(false); // Estado para controlar o modal

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErro("");
    try {
      if (tarefa.id) {
        // Editar
        await api.put(`/tarefas/${tarefa.id}`, {
          nome,
          custo,
          dataLimite,
        });
      } else {
        // Incluir
        await api.post("/tarefas", {
          nome,
          custo,
          dataLimite,
        });
      }
      onClose();
    } catch (error) {
      const errorMessage =
        error.response?.data?.error ||
        "Erro ao salvar tarefa. Verifique os dados.";
      setErro(errorMessage);
      setModalIsOpen(true); // Abre o modal ao capturar o erro
    }
  };

  const closeModal = () => {
    setModalIsOpen(false); // Fecha o modal
  };

  return (
    <div className="modal">
      <form onSubmit={handleSubmit}>
        <h2>{tarefa.id ? "Editar Tarefa" : "Incluir Tarefa"}</h2>

        <div>
          <label>Nome:</label>
          <input
            type="text"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Custo:</label>
          <input
            type="number"
            value={custo}
            onChange={(e) => setCusto(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Data Limite:</label>
          <input
            type="date"
            value={dataLimite}
            onChange={(e) => setDataLimite(e.target.value)}
            required
          />
        </div>
        <button type="submit">Salvar</button>
        <button type="button" onClick={onClose}>
          Cancelar
        </button>
      </form>

      {/* Modal de Erro */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Erro ao salvar"
      >
        <h2 style={{ color: "#f44336" }}>Erro</h2>
        <p>{erro}</p>
        <button
          onClick={closeModal}
          style={{
            padding: "10px 20px",
            backgroundColor: "#f44336",
            color: "#fff",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Fechar
        </button>
      </Modal>
    </div>
  );
};

export default FormularioTarefa;
