import React, { useState } from "react";
import Modal from "react-modal"; // Importa React Modal
import api from "../services/api";

// Estilos do modal
const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    padding: "30px",
    borderRadius: "12px",
    backgroundColor: "#f9f9f9",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)",
    maxWidth: "500px", // Largura máxima do modal
    width: "90%", // Responsivo em telas menores
  },
};

Modal.setAppElement("#root"); // Necessário para acessibilidade

const FormularioTarefa = ({ tarefa, onClose }) => {
  const [nome, setNome] = useState(tarefa.nome || "");
  const [custo, setCusto] = useState(tarefa.custo || "");
  const [dataLimite, setDataLimite] = useState(
    tarefa.dataLimite
      ? new Date(tarefa.dataLimite).toISOString().substr(0, 10)
      : ""
  );
  const [erro, setErro] = useState("");
  const [modalIsOpen, setModalIsOpen] = useState(false); // Estado para controlar o modal

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErro("");
    try {
      if (tarefa.id) {
        // Editar
        await api.put(`/tarefas/${tarefa.id}`, {
          nome,
          custo,
          dataLimite,
        });
      } else {
        // Incluir
        await api.post("/tarefas", {
          nome,
          custo,
          dataLimite,
        });
      }
      onClose();
    } catch (error) {
      const errorMessage =
        error.response?.data?.error ||
        "Erro ao salvar tarefa. Verifique os dados.";
      setErro(errorMessage);
      setModalIsOpen(true); // Abre o modal ao capturar o erro
    }
  };

  const closeModal = () => {
    setModalIsOpen(false); // Fecha o modal
  };

  return (
    <div className="modal">
      <form onSubmit={handleSubmit} style={formStyles}>
        <h2 style={headerStyles}>
          {tarefa.id ? "Editar Tarefa" : "Incluir Tarefa"}
        </h2>

        <div style={inputGroupStyles}>
          <label style={labelStyles}>Nome:</label>
          <input
            type="text"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            required
            style={inputStyles}
          />
        </div>
        <div style={inputGroupStyles}>
          <label style={labelStyles}>Custo:</label>
          <input
            type="number"
            value={custo}
            onChange={(e) => setCusto(e.target.value)}
            required
            style={inputStyles}
          />
        </div>
        <div style={inputGroupStyles}>
          <label style={labelStyles}>Data Limite:</label>
          <input
            type="date"
            value={dataLimite}
            onChange={(e) => setDataLimite(e.target.value)}
            required
            style={inputStyles}
          />
        </div>
        <div style={buttonGroupStyles}>
          <button type="submit" style={submitButtonStyles}>
            Salvar
          </button>
          <button type="button" onClick={onClose} style={cancelButtonStyles}>
            Cancelar
          </button>
        </div>
      </form>

      {/* Modal de Erro */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Erro ao salvar"
      >
        <h2 style={{ color: "#f44336" }}>Erro</h2>
        <p>{erro}</p>
        <button
          onClick={closeModal}
          style={{
            padding: "10px 20px",
            backgroundColor: "#f44336",
            color: "#fff",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Fechar
        </button>
      </Modal>
    </div>
  );
};

// Estilos do formulário
const formStyles = {
  display: "flex",
  flexDirection: "column",
  gap: "20px",
  alignItems: "center", // Centraliza o formulário
};

const headerStyles = {
  marginBottom: "20px",
  textAlign: "center",
  color: "#333",
};

const inputGroupStyles = {
  display: "flex",
  flexDirection: "column", // Empilha label e input verticalmente
  alignItems: "flex-start", // Alinha os elementos à esquerda
  gap: "5px", // Espaçamento entre label e input
  width: "100%",
  maxWidth: "300px", // Largura fixa para inputs
};

const labelStyles = {
  fontSize: "14px",
  fontWeight: "bold",
  color: "#555",
};

const inputStyles = {
  padding: "10px",
  borderRadius: "5px",
  border: "1px solid #ccc",
  fontSize: "14px",
  outline: "none",
  width: "100%", // Preenche a largura disponível no grupo
};

const buttonGroupStyles = {
  display: "flex",
  justifyContent: "space-between",
  width: "100%",
  maxWidth: "300px", // Alinha os botões com os inputs
};

const submitButtonStyles = {
  padding: "10px 20px",
  backgroundColor: "#4CAF50",
  color: "#fff",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
  width: "48%", // Tamanho proporcional
};

const cancelButtonStyles = {
  padding: "10px 20px",
  backgroundColor: "#f44336",
  color: "#fff",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
  width: "48%", // Tamanho proporcional
};

export default FormularioTarefa;
