import React from "react";
import { Draggable } from "@hello-pangea/dnd";
import { FaEdit, FaTrash } from "react-icons/fa"; // Ícones de edição e exclusão

const Tarefa = ({ tarefa, index, onEdit, onDelete }) => {
  const custoNumerico = Number(tarefa.custo); // Converte custo para número

  const estiloDestaque =
    custoNumerico >= 1000
      ? { backgroundColor: "#fff9c4", fontWeight: "bold" } // Destaque para custo >= 1000
      : { backgroundColor: "#f0f0f0" }; // Estilo normal

  return (
    <Draggable draggableId={tarefa.id.toString()} index={index}>
      {(provided) => (
        <div
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          ref={provided.innerRef}
          style={{
            display: "flex",
            flexDirection: "column",
            margin: "8px 0",
            padding: "16px",
            borderRadius: "4px",
            ...estiloDestaque, // Aplica o estilo condicional
            ...provided.draggableProps.style,
          }}
        >
          {/* Nome alinhado à esquerda */}
          <h3 style={{ textAlign: "left", marginBottom: "8px" }}>
            {tarefa.nome}
          </h3>

          {/* Outros detalhes permanecem no layout padrão */}
          <p>ID: {tarefa.id}</p>
          <p>Custo: R$ {custoNumerico.toFixed(2)}</p>
          <p>Data Limite: {tarefa.dataLimite}</p>

          <div style={{ display: "flex", gap: "8px", marginTop: "10px" }}>
            <button
              onClick={onEdit}
              style={{
                border: "none",
                background: "transparent",
                cursor: "pointer",
              }}
            >
              <FaEdit size={20} color="#4CAF50" title="Editar" />
            </button>
            <button
              onClick={onDelete}
              style={{
                border: "none",
                background: "transparent",
                cursor: "pointer",
              }}
            >
              <FaTrash size={20} color="#f44336" title="Excluir" />
            </button>
          </div>
        </div>
      )}
    </Draggable>
  );
};

export default Tarefa;
