// frontend/src/components/Tarefa.js
import React from "react";
import { Draggable } from "react-beautiful-dnd";
import api from "../services/api";

const Tarefa = ({ tarefa, index, onEdit, onDelete }) => {
  const handleDelete = async () => {
    if (window.confirm("Deseja realmente excluir esta tarefa?")) {
      try {
        await api.delete(`/tarefas/${tarefa.id}`);
        onDelete();
      } catch (error) {
        console.error("Erro ao excluir tarefa", error);
      }
    }
  };

  const classeDestaque = tarefa.custo >= 1000 ? "destaque" : "";

  return (
    <Draggable draggableId={`${tarefa.id}`} index={index}>
      {(provided) => (
        <div
          className={`tarefa ${classeDestaque}`}
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
        >
          <p>Nome: {tarefa.nome}</p>
          <p>Custo: R${tarefa.custo.toFixed(2)}</p>
          <p>Data Limite: {new Date(tarefa.dataLimite).toLocaleDateString()}</p>
          <button onClick={onEdit}>Editar</button>
          <button onClick={handleDelete}>Excluir</button>
        </div>
      )}
    </Draggable>
  );
};

export default Tarefa;
