import React from "react";
import { Draggable } from "@hello-pangea/dnd";
import { FaEdit, FaTrash } from "react-icons/fa"; // Ícones de edição e exclusão

const Tarefa = ({ tarefa, index, onEdit, onDelete }) => {
  const custoNumerico = Number(tarefa.custo); // Converte custo para número

  return (
    <Draggable draggableId={tarefa.id.toString()} index={index}>
      {(provided) => (
        <div
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          ref={provided.innerRef}
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "flex-start", // Mantém o alinhamento à esquerda
            margin: "8px 0",
            padding: "16px",
            borderRadius: "4px",
            backgroundColor: "#f0f0f0", // Fundo neutro para todas as tarefas
            ...provided.draggableProps.style,
          }}
        >
          {/* Nome destacado condicionalmente */}
          <h3
            style={{
              color: custoNumerico >= 1000 ? "#ff9800" : "#333", // Destaca o nome com cor laranja
              fontWeight: custoNumerico >= 1000 ? "bold" : "normal", // Torna o nome mais chamativo
              marginBottom: "8px",
            }}
          >
            {tarefa.nome}
          </h3>

          {/* Informações adicionais */}
          <p style={{ margin: "0" }}>
            <strong>ID:</strong> {tarefa.id}
          </p>
          <p style={{ margin: "0" }}>
            <strong>Custo:</strong> R$ {custoNumerico.toFixed(2)}
          </p>
          <p style={{ margin: "0" }}>
            <strong>Data Limite:</strong> {tarefa.dataLimite}
          </p>

          {/* Botões de Ação */}
          <div style={{ marginTop: "8px", display: "flex", gap: "8px" }}>
            <button
              onClick={onEdit}
              style={{
                border: "none",
                background: "transparent",
                cursor: "pointer",
              }}
            >
              <FaEdit size={20} color="#4CAF50" title="Editar" />{" "}
              {/* Ícone de edição */}
            </button>
            <button
              onClick={onDelete}
              style={{
                border: "none",
                background: "transparent",
                cursor: "pointer",
              }}
            >
              <FaTrash size={20} color="#f44336" title="Excluir" />{" "}
              {/* Ícone de exclusão */}
            </button>
          </div>
        </div>
      )}
    </Draggable>
  );
};

export default Tarefa;
