import React, { useEffect, useState } from "react";
import api from "../services/api";
import Tarefa from "./Tarefa";
import FormularioTarefa from "./FormularioTarefa";

import { DragDropContext, Droppable } from "@hello-pangea/dnd";

const ListaTarefas = () => {
  const [tarefas, setTarefas] = useState([]);
  const [editando, setEditando] = useState(null);

  useEffect(() => {
    fetchTarefas();
  }, []);

  const fetchTarefas = async () => {
    try {
      const response = await api.get("/tarefas");
      setTarefas(response.data);
    } catch (error) {
      console.error("Erro ao buscar tarefas", error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await api.delete(`/tarefas/${id}`); // Envia a requisição DELETE
      setTarefas((prevTarefas) =>
        prevTarefas.filter((tarefa) => tarefa.id !== id)
      ); // Atualiza a lista localmente
    } catch (error) {
      console.error("Erro ao excluir tarefa", error);
    }
  };

  const onDragEnd = async (result) => {
    if (!result.destination) return;

    const itens = Array.from(tarefas);
    const [reordenado] = itens.splice(result.source.index, 1);
    itens.splice(result.destination.index, 0, reordenado);

    setTarefas(itens);

    try {
      await api.patch("/tarefas/reordenar", {
        ordem: itens.map((tarefa) => tarefa.id),
      });
    } catch (error) {
      console.error("Erro ao reordenar tarefas", error);
    }
  };

  return (
    <div style={{ padding: "20px", textAlign: "center" }}>
      <h1 style={{ marginBottom: "20px", color: "#333" }}>Lista de Tarefas</h1>

      {editando && (
        <FormularioTarefa
          tarefa={editando}
          onClose={() => {
            setEditando(null);
            fetchTarefas();
          }}
        />
      )}

      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="tarefas">
          {(provided) => (
            <div {...provided.droppableProps} ref={provided.innerRef}>
              {tarefas.map((tarefa, index) => (
                <Tarefa
                  key={tarefa.id}
                  tarefa={tarefa}
                  index={index}
                  onEdit={() => setEditando(tarefa)}
                  onDelete={() => handleDelete(tarefa.id)} // Passa a função de exclusão
                />
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>

      <button
        onClick={() => setEditando({})}
        style={{
          marginTop: "20px",
          padding: "10px 20px",
          backgroundColor: "#4CAF50", // Cor de fundo verde
          color: "#fff", // Texto branco
          border: "none",
          borderRadius: "5px",
          fontSize: "16px",
          cursor: "pointer",
          transition: "background-color 0.3s ease",
        }}
        onMouseOver={(e) => (e.target.style.backgroundColor = "#45a049")} // Efeito de hover
        onMouseOut={(e) => (e.target.style.backgroundColor = "#4CAF50")}
      >
        Incluir
      </button>
    </div>
  );
};

export default ListaTarefas;
